package com.mycompony.study.ch03;

public class ExConditionalOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 삼항 연산자 (조건식) ?A : B
		
		int math = 85;
		
		char grade = (math>90) ? 'lets go home' : 'B';
		System.out.println("grade : " + grade);
		
		// if~else 구문이랑 같음
	}

}
