package com.mycompony.study.ch04;

public class ExIf3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 0;
		
		// 입력하신 3은 짝수 입니다.
		
		if (a % 2 == 0) {
			System.out.println("입력하신 " + a + "는 짝수입니다.");
		} else {
			System.out.println("입력하신 " + a + "는 홀수입니다.");
		}
		
	}

}
